// KEY DISTINCTION:
// n().scale() — numbers are SCALE DEGREE INDICES (0, 1, 2... = root, 2nd, 3rd...)
// note()     — numbers are SEMITONES / half steps (MIDI note numbers)

// These two produce the same note (D):
const x1 = n("0!8".add("<1>")).scale("C:major").s("piano")
// Scale degree 0 = C, add 1 scale degree = scale degree 1 = D

const x2 = note("c3!8".add("<2>")).s("piano")
// C3 = MIDI 48, add 2 semitones = MIDI 50 = D3

// WHY THE DIFFERENCE?
// In C major, C to D is one scale degree but TWO semitones (whole step).
// So +1 scale degree ≠ +1 semitone.

// PRACTICAL IMPLICATIONS:
// Use n().scale() when you want to stay diatonic (in key)
// Use note() when you want chromatic control (exact intervals)

stack(x1, x2)

// 16-bar structure: intro (1-4), build (5-8), drop (9-16)
const enterBuild = "0!4 1!12".slow(16)
const enterDrop = "0!8 1!8".slow(16)

const kick = s("bd!4").gain(0.9)
const bass = n("<0 0 3 5>").scale("C2:minor").s("sawtooth").mask(enterBuild)
const arp = n("0 2 4 7".fast(4)).scale("C4:minor").s("supersaw").mask(enterDrop)

stack(kick, bass, arp)

// WRONG: Alternation where one option creates a leap to B natural
// If Strudel picks G2, we get G2->B2 (major 3rd leap)
// B natural after a leap sounds wrong — in blues it's only a passing tone
const badAlternation = note("[C2 E2 <G2 Bb2> B2]")
  .s("gm_acoustic_bass")
// G2 -> B2 is a leap AND B isn't approached chromatically

// WRONG: Random scale degrees is NOT walking bass
const notWalking = n(irand(6))
  .struct("x*4")
  .scale("C2:blues")
  .s("gm_acoustic_bass")
// This is chaos — no voice leading, no chord outline, not blues

// WRONG: Alternation that uses a note outside the chord
const noThird = note("[F2 <A2 Ab2> C3 B2]")
  .s("gm_acoustic_bass")
// F7 has A natural (major 3rd), not Ab — bar loses its blues identity

// CORRECT: Both alternatives walk stepwise to the next note
// G2->Bb2 is minor 3rd (acceptable in blues), A2->Bb2 is half step
const goodAlternation = note("[C2 E2 <G2 A2> Bb2]")
  .s("gm_acoustic_bass")

// CORRECT: B natural only after Bb (chromatic approach to C)
const correctBNatural = note("[F2 A2 Bb2 B2]")
  .s("gm_acoustic_bass")
// Bb->B->C is the classic blues chromatic walk to the root

// CORRECT: 12-bar blues with safe alternation points
// I(4) - IV(2) - I(2) - V - IV - I - V turnaround
const bassLine = note(`<
  [C2 E2 G2 A2]
  [C2 E2 <G2 A2> Bb2]
  [C2 E2 G2 A2]
  [C2 E2 A2 E2]
  [F2 A2 C3 <D3 Eb3>]
  [F2 A2 Bb2 B2]
  [C2 E2 G2 A2]
  [C2 E2 A2 F#2]
  [G2 B2 A2 G2]
  [F2 A2 Bb2 B2]
  [C2 E2 <G2 A2> F#2]
  [G2 B2 D3 B2]
>`)
  .s("gm_acoustic_bass")
  .gain("0.85 0.7 0.75 0.7")
  .lpf(900)
// Safe alternation points for blues:
// - Bar 2 beat 3: G2 or A2, both step to Bb2
// - Bar 5 beat 4: D3 or Eb3, both valid dominant 7th extensions
// - Bar 11 beat 3: G2 or A2, both step down to F#2

bassLine

// WRONG: Jump from D to F (minor 3rd gap)
// Bar 4 ends on D, bar 5 starts on F — no passing tone
const badBar4 = "c3 e3 eb3 d3"
const bar5 = "f2 a2 c3 eb3"
// Listener expects stepwise motion but hears a skip

// CORRECT: End bar 4 on E (half step below F)
const goodBar4 = "c3 bb2 ab2 e2"  // chromatic descent landing on approach note
// E -> F is a half step — smooth voice leading

// BLUES CHROMATIC APPROACHES:
// Approaching C from below: a -> bb -> b -> c (use flat 7, not natural 7)
// Approaching F from below: d -> eb -> e -> f
// Approaching G from below: e -> f -> gb -> g (or f#)

// WRONG: Using natural 7 approach (sounds too "clean", not bluesy)
const cleanApproach = "g2 a2 b2 c3"  // works but lacks blues character

// CORRECT: Flat 7 approach for blues feel
const bluesApproach = "g2 a2 bb2 b2"  // bb is the blue note, b is the leading tone
// The bb -> b -> c motion is characteristic of blues bass

note(cat(goodBar4, bar5)).s("sawtooth").lpf(400)

// WRONG: .slow(12) makes each of the 12 elements last 12 cycles = 144 cycles total
// const badRoots = "<0!4 3!2 0!2 4 3 0 4>"
// n(badRoots).slow(12)  // 144 cycles, not 12!

// CORRECT: <> alternation already gives one element per cycle
// 12 elements = 12 bars, no .slow() needed

// 12-bar blues in C: I(4) IV(2) I(2) V IV I V
// Using chord() for pad voicings — parallel motion is fine here
const progression = chord("<C!4 F!2 C!2 G F C G>")
  .voicing()
  .s("supersaw")
  .room(0.3)

progression

// CORRECT: single quotes only
'c4' + ':major'  // → "c4:major"

// WRONG: double quotes are mini-notation
// "c4" + ':major'   // fails — "c4" is transpiled
// 'c4' + ":major"   // fails — ":major" is transpiled
// "c4" + ":major"   // fails — both are transpiled

// WRONG: Using .add() to transpose a pattern
// This preserves intervallic contour — it's arpeggiation, not walking
const chordRoots = "<0!4 3!2 0!2 4 3 0 4>"
const arpeggioPattern = "0 2 4 5"
const notWalking = n(arpeggioPattern)
  .add(n(chordRoots))
  .scale("E2:blues")
  .s("sawtooth")
// Over I chord: E A B D (jumping by 3rds)
// Over IV chord: A D E G (still jumping — not stepwise!)

// CORRECT: Write actual walking lines with chromatic passing tones
// Use note() with explicit note names for chromatic control
// Use cat() to sequence bars

// 12-bar blues in C: C/F/C/C/F/F/C/C/G/F/C/G
const walkingBassBars = [
  "c3 bb2 a2 g2",
  "f2 a2 a#2 b2",
  "c3 e3 g3 bb3",
  "c4 bb3 a3 g3",
  "f3 eb3 c3 a2",
  "f2 a2 bb2 b2",
  "c3 e3 f3 gb3",
  "g3 a3 bb3 c4",
  "g3 f3 e3 g3",
  "f3 eb3 d3 db3",
  "c3 bb2 a2 ab2",
  "g2 a2 bb2 b2",
]

const walkingBass = note(cat(...walkingBassBars))
  .s("sawtooth")

walkingBass
